if hasattr(VR, 'container2_label'): container2_count = VR.containers[2].getCount() text2 = 'Füllstand: ' + str(container2_count) + " / " + str(VR.containers[2].getCapacity()) VR.container2_label.setText(text2) VR.container2_label.setSize(len(text2)*0.1,0.2)
if hasattr(VR, 'fertiglabel'): fertig_count = VR.containers[3].getCount() textLager = 'Fertig: ' + str(fertig_count) VR.fertiglabel.setText(textLager) VR.fertiglabel.setSize(len(textLager)*0.15,0.3)
